import React, { useEffect, useState } from 'react';
import { Box, Typography, Card, CardContent, Grid, TextField, Button, IconButton, Tooltip } from '@mui/material';
import { credentialsService, StoredCredentials, IntegrationName } from '../services/credentials';
import { VisibilityOff as HideIcon, Save as SaveIcon, Delete as DeleteIcon } from '@mui/icons-material';

const integrationLabels: Record<IntegrationName, string> = {
  bigdata: 'BigData Corp',
  wiza: 'Wiza',
  surfe: 'Surfe',
  pdl: 'People Data Labs'
};

const IntegrationKeys: React.FC = () => {
  const [credentials, setCredentials] = useState<StoredCredentials>({});
  const [editing, setEditing] = useState<IntegrationName | null>(null);
  const [tempValue, setTempValue] = useState('');

  useEffect(() => {
    setCredentials(credentialsService.getAll());
  }, []);

  const handleEdit = (name: IntegrationName) => {
    setEditing(name);
    setTempValue(credentials[name] || '');
  };

  const handleSave = (name: IntegrationName) => {
    credentialsService.save(name, tempValue.trim());
    setCredentials(credentialsService.getAll());
    setEditing(null);
  };

  const handleDelete = (name: IntegrationName) => {
    credentialsService.remove(name);
    setCredentials(credentialsService.getAll());
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>API Keys</Typography>
      <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
        Manage and securely store your integration API keys locally in your browser. Keys are saved in localStorage.
      </Typography>
      <Grid container spacing={2}>
        {(Object.keys(integrationLabels) as IntegrationName[]).map((name) => (
          <Grid item xs={12} sm={6} md={4} key={name}>
            <Card variant="outlined">
              <CardContent>
                <Typography variant="h6" gutterBottom>{integrationLabels[name]}</Typography>
                {editing === name ? (
                  <TextField
                    fullWidth
                    label="API Key"
                    type="password"
                    value={tempValue}
                    onChange={(e) => setTempValue(e.target.value)}
                    sx={{ mb: 2 }}
                  />
                ) : (
                  <TextField
                    fullWidth
                    label="API Key"
                    type="password"
                    value={credentials[name] || ''}
                    InputProps={{ readOnly: true }}
                    sx={{ mb: 2 }}
                  />
                )}
                {editing === name ? (
                  <Button
                    variant="contained"
                    startIcon={<SaveIcon />}
                    onClick={() => handleSave(name)}
                    disabled={!tempValue.trim()}
                  >
                    Save
                  </Button>
                ) : (
                  <Box>
                    <Button variant="outlined" onClick={() => handleEdit(name)} sx={{ mr: 1 }}>Edit</Button>
                    {credentials[name] && (
                      <Tooltip title="Remove key">
                        <IconButton color="error" onClick={() => handleDelete(name)}>
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    )}
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default IntegrationKeys;

import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  Card, 
  CardContent, 
  Grid, 
  Button, 
  TextField,
  CircularProgress,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from '@mui/material';
import { 
  Api as ApiIcon,
  Person as PersonIcon,
  Business as BusinessIcon,
  Search as SearchIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  ExpandMore as ExpandMoreIcon,
  Settings as SettingsIcon,
  Security as SecurityIcon,
  LinkedIn as LinkedInIcon,
  Email as EmailIcon,
  AccountBalance as CreditIcon,
  Groups as GroupsIcon,
  Phone as PhoneIcon,
  FilterList as FilterIcon,
  Storage as DatabaseIcon,
  Work as WorkIcon
} from '@mui/icons-material';
import { 
  bigDataCorpService, 
  validateCPF, 
  validateCNPJ, 
  formatCPF, 
  formatCNPJ,
  type CPFPersonData,
  type CNPJCompanyData,
  type BigDataCorpCredentials
} from '../services/bigdata-corp';
import {
  wizaService,
  validateLinkedInUrl,
  validateEmailDomain,
  type LinkedInProfileData,
  type EmailFinderData,
  type CompanyEnrichmentData,
  type WizaCredentials
} from '../services/wiza';
import {
  surfeService,
  validatePersonData,
  validateCompanyData,
  validateEmailAddress,
  validatePhoneNumber,
  type PersonSearchData,
  type PersonEnrichData,
  type CompanySearchData,
  type CompanyEnrichData as SurfeCompanyEnrichData,
  type SurfeCredentials,
  type SurfeCreditsData
} from '../services/surfe';
import {
  peopleDataLabsService,
  validatePersonEnrichParams,
  validateCompanyEnrichParams,
  validateEmail,
  type PersonData as PDLPersonData,
  type CompanyData as PDLCompanyData,
  type PDLCredentials,
  type PDLCreditsData,
  type PersonEnrichRequest,
  type PersonSearchRequest,
  type CompanyEnrichRequest,
  type CompanySearchRequest
} from '../services/people-data-labs';

const IntegrationCard: React.FC<{
  title: string;
  description: string;
  icon: React.ReactNode;
  status: 'connected' | 'disconnected' | 'error';
  onConfigure: () => void;
}> = ({ title, description, icon, status, onConfigure }) => (
  <Card sx={{ height: '100%', position: 'relative' }}>
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {icon}
        <Typography variant="h6" component="div" sx={{ ml: 1 }}>
          {title}
        </Typography>
        <Box sx={{ ml: 'auto' }}>
          <Chip 
            label={status} 
            color={status === 'connected' ? 'success' : status === 'error' ? 'error' : 'default'}
            size="small"
            icon={status === 'connected' ? <CheckIcon /> : <ErrorIcon />}
          />
        </Box>
      </Box>

      <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
        {description}
      </Typography>

      <Button 
        variant="outlined" 
        startIcon={<SettingsIcon />}
        onClick={onConfigure}
        fullWidth
      >
        Configure
      </Button>
    </CardContent>
  </Card>
);

const BigDataCorpConfigDialog: React.FC<{
  open: boolean;
  onClose: () => void;
  onSave: (credentials: BigDataCorpCredentials) => void;
}> = ({ open, onClose, onSave }) => {
  const [credentials, setCredentials] = useState<BigDataCorpCredentials>({
    apiKey: '',
    apiSecret: ''
  });

  const handleSave = () => {
    if (credentials.apiKey && credentials.apiSecret) {
      onSave(credentials);
      onClose();
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <SecurityIcon sx={{ mr: 1 }} />
          BigData Corp API Configuration
        </Box>
      </DialogTitle>
      <DialogContent>
        <Alert severity="info" sx={{ mb: 2 }}>
          Enter your BigData Corp API credentials. These will be stored securely and used for data enrichment.
        </Alert>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid size={12}>
            <TextField
              fullWidth
              label="API Key"
              type="password"
              value={credentials.apiKey}
              onChange={(e) => setCredentials(prev => ({ ...prev, apiKey: e.target.value }))}
              required
            />
          </Grid>
          <Grid size={12}>
            <TextField
              fullWidth
              label="API Secret"
              type="password"
              value={credentials.apiSecret}
              onChange={(e) => setCredentials(prev => ({ ...prev, apiSecret: e.target.value }))}
              required
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button 
          variant="contained" 
          onClick={handleSave}
          disabled={!credentials.apiKey || !credentials.apiSecret}
        >
          Save & Test Connection
        </Button>
      </DialogActions>
    </Dialog>
  );
};

const WizaConfigDialog: React.FC<{
  open: boolean;
  onClose: () => void;
  onSave: (credentials: WizaCredentials) => void;
}> = ({ open, onClose, onSave }) => {
  const [credentials, setCredentials] = useState<WizaCredentials>({
    apiKey: ''
  });
  const [credits, setCredits] = useState<number | null>(null);
  const [testing, setTesting] = useState(false);

  const handleSave = async () => {
    if (credentials.apiKey) {
      setTesting(true);
      try {
        wizaService.setCredentials(credentials);
        const creditsResponse = await wizaService.getCredits();
        if (creditsResponse.success) {
          setCredits(creditsResponse.data.credits);
          onSave(credentials);
          onClose();
        } else {
          throw new Error('Invalid API key');
        }
      } catch (error) {
        console.error('Failed to validate Wiza API key:', error);
      } finally {
        setTesting(false);
      }
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <LinkedInIcon sx={{ mr: 1 }} />
          Wiza API Configuration
        </Box>
      </DialogTitle>
      <DialogContent>
        <Alert severity="info" sx={{ mb: 2 }}>
          Enter your Wiza API key. This will be used for LinkedIn profile enrichment and email finding.
          Visit <a href="https://wiza.co/api-docs" target="_blank" rel="noopener noreferrer">Wiza API Docs</a> for more information.
        </Alert>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid size={12}>
            <TextField
              fullWidth
              label="API Key"
              type="password"
              value={credentials.apiKey}
              onChange={(e) => setCredentials(prev => ({ ...prev, apiKey: e.target.value }))}
              required
              helperText="Get your API key from Wiza dashboard"
            />
          </Grid>
          {credits !== null && (
            <Grid size={12}>
              <Alert severity="success">
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <CreditIcon sx={{ mr: 1 }} />
                  Available Credits: {credits}
                </Box>
              </Alert>
            </Grid>
          )}
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button 
          variant="contained" 
          onClick={handleSave}
          disabled={!credentials.apiKey || testing}
          startIcon={testing ? <CircularProgress size={20} /> : null}
        >
          {testing ? 'Testing...' : 'Save & Test Connection'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

const EnrichmentDialog: React.FC<{
  open: boolean;
  onClose: () => void;
  type: 'cpf' | 'cnpj';
}> = ({ open, onClose, type }) => {
  const [document, setDocument] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<CPFPersonData | CNPJCompanyData | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleEnrich = async () => {
    if (!document) return;

    setLoading(true);
    setError(null);
    setResult(null);

    try {
      if (type === 'cpf') {
        if (!validateCPF(document)) {
          throw new Error('Invalid CPF format');
        }
        const response = await bigDataCorpService.enrichContactByCPF({ document });
        if (response.success) {
          setResult(response.data);
        } else {
          throw new Error(response.errors?.[0] || 'Enrichment failed');
        }
      } else {
        if (!validateCNPJ(document)) {
          throw new Error('Invalid CNPJ format');
        }
        const response = await bigDataCorpService.enrichCompanyByCNPJ({ document });
        if (response.success) {
          setResult(response.data);
        } else {
          throw new Error(response.errors?.[0] || 'Enrichment failed');
        }
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setDocument('');
    setResult(null);
    setError(null);
    onClose();
  };

  return (
    <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
      <DialogTitle>
        Enrich {type === 'cpf' ? 'Contact (CPF)' : 'Company (CNPJ)'}
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid size={12} sm={8}>
            <TextField
              fullWidth
              label={type === 'cpf' ? 'CPF' : 'CNPJ'}
              value={document}
              onChange={(e) => setDocument(e.target.value)}
              placeholder={type === 'cpf' ? '000.000.000-00' : '00.000.000/0000-00'}
              helperText={`Enter a valid ${type.toUpperCase()} to enrich data`}
            />
          </Grid>
          <Grid size={12} sm={4}>
            <Button
              fullWidth
              variant="contained"
              startIcon={loading ? <CircularProgress size={20} /> : <SearchIcon />}
              onClick={handleEnrich}
              disabled={loading || !document}
              sx={{ height: '56px' }}
            >
              {loading ? 'Enriching...' : 'Enrich'}
            </Button>
          </Grid>
        </Grid>

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        {result && (
          <Box sx={{ mt: 3 }}>
            <Typography variant="h6" gutterBottom>
              Enrichment Results
            </Typography>
            <Card variant="outlined">
              <CardContent>
                {type === 'cpf' ? (
                  <CPFResultDisplay data={result as CPFPersonData} />
                ) : (
                  <CNPJResultDisplay data={result as CNPJCompanyData} />
                )}
              </CardContent>
            </Card>
          </Box>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>Close</Button>
        {result && (
          <Button variant="contained">
            Create {type === 'cpf' ? 'Contact' : 'Company'}
          </Button>
        )}
      </DialogActions>
    </Dialog>
  );
};

const CPFResultDisplay: React.FC<{ data: CPFPersonData }> = ({ data }) => (
  <Grid container spacing={2}>
    <Grid size={12}>
      <Typography variant="h6">{data.name}</Typography>
      <Typography variant="body2" color="text.secondary">
        CPF: {formatCPF(data.cpf)}
      </Typography>
      {data.birthdate && (
        <Typography variant="body2" color="text.secondary">
          Birth Date: {data.birthdate}
        </Typography>
      )}
    </Grid>

    {data.emails && data.emails.length > 0 && (
      <Grid size={12} sm={6}>
        <Typography variant="subtitle2" gutterBottom>Emails</Typography>
        <List dense>
          {data.emails.map((email, index) => (
            <ListItem key={index}>
              <ListItemText 
                primary={email.email} 
                secondary={`${email.type} (${email.confidence}% confidence)`}
              />
            </ListItem>
          ))}
        </List>
      </Grid>
    )}

    {data.phones && data.phones.length > 0 && (
      <Grid size={12} sm={6}>
        <Typography variant="subtitle2" gutterBottom>Phones</Typography>
        <List dense>
          {data.phones.map((phone, index) => (
            <ListItem key={index}>
              <ListItemText 
                primary={phone.number} 
                secondary={`${phone.type} (${phone.confidence}% confidence)`}
              />
            </ListItem>
          ))}
        </List>
      </Grid>
    )}

    {data.professional_data && (
      <Grid size={12}>
        <Typography variant="subtitle2" gutterBottom>Professional Data</Typography>
        <Box>
          {data.professional_data.company && (
            <Typography variant="body2">Company: {data.professional_data.company}</Typography>
          )}
          {data.professional_data.position && (
            <Typography variant="body2">Position: {data.professional_data.position}</Typography>
          )}
          {data.professional_data.income_range && (
            <Typography variant="body2">Income Range: {data.professional_data.income_range}</Typography>
          )}
        </Box>
      </Grid>
    )}
  </Grid>
);

const CNPJResultDisplay: React.FC<{ data: CNPJCompanyData }> = ({ data }) => (
  <Grid container spacing={2}>
    <Grid size={12}>
      <Typography variant="h6">{data.company_name}</Typography>
      {data.trade_name && (
        <Typography variant="body1" color="text.secondary">
          Trade Name: {data.trade_name}
        </Typography>
      )}
      <Typography variant="body2" color="text.secondary">
        CNPJ: {formatCNPJ(data.cnpj)}
      </Typography>
      <Typography variant="body2" color="text.secondary">
        Status: {data.status}
      </Typography>
    </Grid>

    {data.industry && (
      <Grid size={12} sm={6}>
        <Typography variant="subtitle2">Industry</Typography>
        <Typography variant="body2">{data.industry}</Typography>
      </Grid>
    )}

    {data.website && (
      <Grid size={12} sm={6}>
        <Typography variant="subtitle2">Website</Typography>
        <Typography variant="body2">{data.website}</Typography>
      </Grid>
    )}

    {data.shareholders && data.shareholders.length > 0 && (
      <Grid size={12}>
        <Typography variant="subtitle2" gutterBottom>Shareholders</Typography>
        <List dense>
          {data.shareholders.map((shareholder, index) => (
            <ListItem key={index}>
              <ListItemText 
                primary={shareholder.name} 
                secondary={`${shareholder.participation}% participation`}
              />
            </ListItem>
          ))}
        </List>
      </Grid>
    )}
  </Grid>
);

const WizaEnrichmentDialog: React.FC<{
  open: boolean;
  onClose: () => void;
  type: 'linkedin' | 'email';
}> = ({ open, onClose, type }) => {
  const [linkedInUrl, setLinkedInUrl] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [companyDomain, setCompanyDomain] = useState('');
  const [loading, setLoading] = useState(false);
  const [linkedInResult, setLinkedInResult] = useState<LinkedInProfileData | null>(null);
  const [emailResult, setEmailResult] = useState<EmailFinderData[] | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [creditsUsed, setCreditsUsed] = useState<number | null>(null);

  const handleEnrich = async () => {
    if (type === 'linkedin' && !linkedInUrl) return;
    if (type === 'email' && (!firstName || !lastName || !companyDomain)) return;

    setLoading(true);
    setError(null);
    setLinkedInResult(null);
    setEmailResult(null);

    try {
      if (type === 'linkedin') {
        if (!validateLinkedInUrl(linkedInUrl)) {
          throw new Error('Invalid LinkedIn URL format');
        }
        const response = await wizaService.enrichLinkedInProfile({ 
          linkedin_url: linkedInUrl,
          include_emails: true,
          include_phone: true
        });
        if (response.success) {
          setLinkedInResult(response.data);
          setCreditsUsed(response.credits_used || null);
        } else {
          throw new Error(response.errors?.[0] || 'LinkedIn enrichment failed');
        }
      } else {
        if (!validateEmailDomain(companyDomain)) {
          throw new Error('Invalid company domain format');
        }
        const response = await wizaService.findEmail({
          first_name: firstName,
          last_name: lastName,
          company_domain: companyDomain,
          linkedin_url: linkedInUrl || undefined
        });
        if (response.success) {
          setEmailResult(response.data);
          setCreditsUsed(response.credits_used || null);
        } else {
          throw new Error(response.errors?.[0] || 'Email finding failed');
        }
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setLinkedInUrl('');
    setFirstName('');
    setLastName('');
    setCompanyDomain('');
    setLinkedInResult(null);
    setEmailResult(null);
    setError(null);
    setCreditsUsed(null);
    onClose();
  };

  return (
    <Dialog open={open} onClose={handleClose} maxWidth="lg" fullWidth>
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          {type === 'linkedin' ? <LinkedInIcon sx={{ mr: 1 }} /> : <EmailIcon sx={{ mr: 1 }} />}
          {type === 'linkedin' ? 'Enrich LinkedIn Profile' : 'Find Email Address'}
        </Box>
      </DialogTitle>
      <DialogContent>
        {type === 'linkedin' ? (
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid size={12} sm={8}>
              <TextField
                fullWidth
                label="LinkedIn Profile URL"
                value={linkedInUrl}
                onChange={(e) => setLinkedInUrl(e.target.value)}
                placeholder="https://linkedin.com/in/profile-name"
                helperText="Enter a valid LinkedIn profile URL"
              />
            </Grid>
            <Grid size={12} sm={4}>
              <Button
                fullWidth
                variant="contained"
                startIcon={loading ? <CircularProgress size={20} /> : <SearchIcon />}
                onClick={handleEnrich}
                disabled={loading || !linkedInUrl}
                sx={{ height: '56px' }}
              >
                {loading ? 'Enriching...' : 'Enrich'}
              </Button>
            </Grid>
          </Grid>
        ) : (
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid size={12} sm={4}>
              <TextField
                fullWidth
                label="First Name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
              />
            </Grid>
            <Grid size={12} sm={4}>
              <TextField
                fullWidth
                label="Last Name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
              />
            </Grid>
            <Grid size={12} sm={4}>
              <TextField
                fullWidth
                label="Company Domain"
                value={companyDomain}
                onChange={(e) => setCompanyDomain(e.target.value)}
                placeholder="company.com"
                required
              />
            </Grid>
            <Grid size={12} sm={8}>
              <TextField
                fullWidth
                label="LinkedIn URL (Optional)"
                value={linkedInUrl}
                onChange={(e) => setLinkedInUrl(e.target.value)}
                placeholder="https://linkedin.com/in/profile-name"
                helperText="Optional: LinkedIn profile URL for better accuracy"
              />
            </Grid>
            <Grid size={12} sm={4}>
              <Button
                fullWidth
                variant="contained"
                startIcon={loading ? <CircularProgress size={20} /> : <SearchIcon />}
                onClick={handleEnrich}
                disabled={loading || !firstName || !lastName || !companyDomain}
                sx={{ height: '56px' }}
              >
                {loading ? 'Finding...' : 'Find Email'}
              </Button>
            </Grid>
          </Grid>
        )}

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        {creditsUsed && (
          <Alert severity="info" sx={{ mt: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <CreditIcon sx={{ mr: 1 }} />
              Credits used: {creditsUsed}
            </Box>
          </Alert>
        )}

        {linkedInResult && (
          <Box sx={{ mt: 3 }}>
            <Typography variant="h6" gutterBottom>
              LinkedIn Profile Results
            </Typography>
            <Card variant="outlined">
              <CardContent>
                <LinkedInProfileResultDisplay data={linkedInResult} />
              </CardContent>
            </Card>
          </Box>
        )}

        {emailResult && emailResult.length > 0 && (
          <Box sx={{ mt: 3 }}>
            <Typography variant="h6" gutterBottom>
              Email Finding Results
            </Typography>
            <Grid container spacing={2}>
              {emailResult.map((email, index) => (
                <Grid size={12} sm={6} key={index}>
                  <Card variant="outlined">
                    <CardContent>
                      <EmailResultDisplay data={email} />
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>Close</Button>
        {(linkedInResult || (emailResult && emailResult.length > 0)) && (
          <Button variant="contained">
            Create {type === 'linkedin' ? 'Contact' : 'Contact with Email'}
          </Button>
        )}
      </DialogActions>
    </Dialog>
  );
};

const LinkedInProfileResultDisplay: React.FC<{ data: LinkedInProfileData }> = ({ data }) => (
  <Grid container spacing={2}>
    <Grid size={12}>
