    school: string;
    degree?: string;
    field_of_study?: string;
    duration?: string;
  }>;
  skills?: string[];
  emails?: Array<{
    email: string;
    type: 'work' | 'personal';
    confidence: number;
    source: string;
  }>;
  phones?: Array<{
    number: string;
    type: 'work' | 'mobile' | 'home';
    confidence: number;
  }>;
  social_profiles?: {
    twitter?: string;
    facebook?: string;
    instagram?: string;
    github?: string;
  };
  profile_image_url?: string;
  connections_count?: number;
  followers_count?: number;
}

export interface EmailFinderData {
  email: string;
  confidence: number;
  type: 'work' | 'personal';
  source: string;
  verification_status: 'valid' | 'invalid' | 'unknown' | 'risky';
  first_name: string;
  last_name: string;
  company_name?: string;
  title?: string;
  linkedin_url?: string;
}

export interface CompanyEnrichmentData {
  company_name: string;
  domain: string;
  linkedin_url?: string;
  website?: string;
  industry?: string;
  company_size?: string;
  founded_year?: number;
  headquarters?: {
    city: string;
    state?: string;
    country: string;
    address?: string;
  };
  description?: string;
  specialties?: string[];
  employee_count?: number;
  revenue_range?: string;
  social_profiles?: {
    linkedin?: string;
    twitter?: string;
    facebook?: string;
